import React, { Component } from "react";
import Auth from "../Auth";
import { Redirect } from "react-router-dom";

class Login extends Component {
  state = {
    redirect: false,
  };

  componentDidMount() {
    this.setState({ redirect: false });
  }
  login = () => {
    Auth.login(() => {
      this.setState({ redirect: true });
    });
  };
}

export default Login;
