import React from "react";
import { Route, Redirect } from "react-router-dom";
import Auth from "./Auth";

const privateRoute = ({ component: Component, ...rest }) => {
  <Route
    {...rest}
    render={({ location }) =>
      Auth.login ? (
        <Component />
      ) : (
        <Redirect
          to={{
            pathname: "/login",
            state: { from: location },
          }}
        />
      )
    }
  />;
};

export default privateRoute;
